{
  "description": "Extract entities, detect language and extract key-phrases",
  "cognitiveServices": {
    	"@odata.type": "#Microsoft.Azure.Search.CognitiveServicesByKey",
    	"description": "mycogsvcs",
    	"key": "{{cog_services_key}}"
  },
  "skills":
  [
  	{
        "@odata.type": "#Microsoft.Skills.Vision.OcrSkill",
        "context": "/document/normalized_images/*",
        "defaultLanguageCode": "en",
        "detectOrientation": true,
        "inputs": [
          {
            "name": "image",
            "source": "/document/normalized_images/*"
          }
        ],
        "outputs": [
          {
            "name": "text"
          }
        ]
    },
    {
      "@odata.type": "#Microsoft.Skills.Text.MergeSkill",
      "description": "Create merged_text, which includes all the textual representation of each image inserted at the right location in the content field.",
      "context": "/document",
      "insertPreTag": " ",
      "insertPostTag": " ",
      "inputs": [
        {
          "name":"text", 
          "source": "/document/content"
        },
        {
          "name": "itemsToInsert", 
          "source": "/document/normalized_images/*/text"
        },
        {
          "name":"offsets", 
          "source": "/document/normalized_images/*/contentOffset" 
        }
      ],
      "outputs": [
        {
          "name": "mergedText", 
          "targetName" : "merged_text"
        }
      ]
    },
    {
      "@odata.type": "#Microsoft.Skills.Text.SplitSkill",
      "textSplitMode": "pages",
      "maximumPageLength": 4000,
      "defaultLanguageCode": "en",
      "context": "/document",
      "inputs": [
        {
          "name": "text",
          "source": "/document/merged_text"
        }
      ],
      "outputs": [
        {
          "name": "textItems",
          "targetName": "pages"
        }
      ]
    },
    {
      "@odata.type": "#Microsoft.Skills.Text.KeyPhraseExtractionSkill",
      "context": "/document/pages/*",
      "inputs": [
        {
          "name": "text",
          "source": "/document/pages/*"
        }
      ],
      "outputs": [
        {
          "name": "keyPhrases",
          "targetName": "keyPhrases"
        }
      ]
    },
    {
      "@odata.type": "#Microsoft.Skills.Text.LanguageDetectionSkill",
      "context": "/document",
      "inputs": [
        {
          "name": "text",
          "source": "/document/merged_text"
        }
      ],
      "outputs": [
        {
          "name": "languageCode",
          "targetName": "language"
        }
      ]
    },
    {
      "@odata.type": "#Microsoft.Skills.Text.TranslationSkill",
      "context": "/document/merged_text",
      "defaultToLanguageCode": "en",
      "inputs": [
        {
          "name": "text",
          "source": "/document/merged_text"
        }
      ],
      "outputs": [
        {
          "name": "translatedText",
          "targetName": "translated_text"
        }
      ]
    },
    {
      "@odata.type": "#Microsoft.Skills.Text.V3.EntityRecognitionSkill",
      "context": "/document/pages/*",
      "categories": ["Organization", "Location", "Person"],
      "minimumPrecision": 0.7,
      "inputs": [
        {
          "name": "text",
          "source": "/document/pages/*"
        },
        {
            "name": "languageCode", 
            "source": "/document/language"
        }
      ],
      "outputs": [
        {
          "name": "organizations",
          "targetName": "organizations"
        },
        {
          "name": "locations",
          "targetName": "locations"
        },
        {
          "name": "persons",
          "targetName": "persons"
        }
      ]
    },
    {
        "@odata.type": "#Microsoft.Skills.Custom.WebApiSkill",
        "name": "summary-custom-skill",
        "description": "Short summary OpenAI generated",
        "context": "/document/merged_text",
        "uri": "{{azure_functions_endpoint}}",
        "httpMethod": "POST",
        "timeout": "PT30S",
        "batchSize": 1,
        "degreeOfParallelism": 1,
        "inputs": [
          {
            "name": "text",
            "source": "/document/merged_text"
          }
        ],
        "outputs": [
          {
            "name": "summary",
            "targetName": "summary"
          }
        ],
        "httpHeaders": {
          "x-functions-key": "{{function_key}}"
        }
      }
  ],
"knowledgeStore": {
  "storageConnectionString": "DefaultEndpointsProtocol=https;AccountName=wfpsearchdemostorage;AccountKey=Ch27MD6IFD+FYQ8rOIy7LreEJ55LmIoKsOrcw7tAjbJ754XA4ssX0N+xOWQVP3ud8vXzr/XUGiz/+AStGMHtPw==;EndpointSuffix=core.windows.net",
      "projections": [
        {
          "tables": [
            {
              "tableName": "WFPSearchSkillsetDocument",
              "generatedKeyName": "Documentid",
              "source": "/document/metadata_storage_path"
            },
            {
              "tableName": "WFPSearchSkillsetPages",
              "generatedKeyName": "Pagesid",
              "source": "/document/pages/*",
              "sourceContext": null
            },
            {
              "tableName": "WFPSearchSkillsetKeyPhrases",
              "referenceKeyName": null,
              "generatedKeyName": "KeyPhrasesid",
              "source": null,
              "sourceContext": "/document/pages/*/keyPhrases/*",
              "inputs": [
                {
                  "name": "keyphrases",
                  "source": "/document/pages/*/keyPhrases/*",
                  "sourceContext": null,
                  "inputs": []
                }
              ]
            },
            {
              "tableName": "WFPSearchSkillsetLocation",
              "referenceKeyName": null,
              "generatedKeyName": "Locationsid",
              "source": null,
              "sourceContext": "/document/pages/*/locations/*",
              "inputs": [
                {
                  "name": "locations",
                  "source": "/document/pages/*/locations/*",
                  "sourceContext": null,
                  "inputs": []
                }
                ]
            },
            {
              "tableName": "WFPSearchSkillsetOrg",
              "referenceKeyName": null,
              "generatedKeyName": "Orgid",
              "source": null,
              "sourceContext": "/document/pages/*/organizations/*",
              "inputs": [                {
                  "name": "organizations",
                  "source": "/document/pages/*/organizations/*",
                  "sourceContext": null,
                  "inputs": []
                }]
            },
            {
              "tableName": "WFPSearchSkillsetPerson",
              "referenceKeyName": null,
              "generatedKeyName": "Personsid",
              "source": null,
              "sourceContext": "/document/pages/*/persons/*",
              "inputs": [
                {
                  "name": "persons",
                  "source": "/document/pages/*/persons/*",
                  "sourceContext": null,
                  "inputs": []
                }
              ]
            },
            {
              "tableName": "azureblobSkillsetImages",
              "referenceKeyName": null,
              "generatedKeyName": "Imagesid",
              "source": "/document/normalized_images/*",
              "sourceContext": null,
              "inputs": []
            }
          ],
          "objects": [],
          "files": []
        }
      ],
      "parameters": {
        "synthesizeGeneratedKeyName": true
      }
  }
}
